import React, { useState, useEffect, useCallback } from 'react';
import { Save, Edit } from 'lucide-react';
import io from 'socket.io-client';
import FileTree from './components/FileTree';
import Editor from './components/Editor';
import ChatBox from './components/ChatBox';
import { transitServerApi, cloudDriveApi } from './apiClient';

function App() {
  const [fileSystem, setFileSystem] = useState([]); // 文件系统状态
  const [selectedFile, setSelectedFile] = useState(null); // 选中的文件
  const [aiModifiedContent, setAiModifiedContent] = useState(''); // AI 修改的内容
  const [messages, setMessages] = useState([]); // 聊天消息

  useEffect(() => {
    const existingUserId = sessionStorage.getItem('userId');
    if (!existingUserId) {
      const newUserId = `user_${Date.now()}_${Math.random().toString(36).slice(2, 11)}`;
      sessionStorage.setItem('userId', newUserId);
    }

    fetchInitialFileSystem();

    const socket = io('/cloud-drive-service', {
      path: '/socket.io'
    });
    socket.on('fileSystemUpdate', (updatedFileSystem) => {
      setFileSystem(updatedFileSystem);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const fetchInitialFileSystem = async () => {
    try {
      const response = await cloudDriveApi.getInitialFileSystem();
      const data = response.data;
      setFileSystem(data);
    } catch (error) {
      console.error('Error fetching initial file system:', error);
    }
  };

  const handleSaveContent = useCallback(async (content) => {
    if (!selectedFile) return;

    try {
      await cloudDriveApi.saveFile({
        file: {
          id: selectedFile.id,
          content: content
        }
      });

      setSelectedFile(prevFile => ({ ...prevFile, content }));
      updateFileSystem(selectedFile.id, { ...selectedFile, content });
    } catch (error) {
      console.error('Error saving file:', error);
    }
  }, [selectedFile]);

  const handleSelectFile = (file) => {
    setSelectedFile(file);
    setAiModifiedContent('');
  };

  const handleSendMessage = async (message) => {
    const userId = sessionStorage.getItem('userId');
    const newMessage = {
      sender: userId,
      content: message,
      timestamp: new Date().toISOString(),
      file: selectedFile ? selectedFile.id : null
    };

    setMessages(prevMessages => [...prevMessages, newMessage]);

    try {
      const response = await transitServerApi.getChatContent(newMessage);
      setMessages(prevMessages => [...prevMessages, { sender: 'ai', content: response.data.content }]);
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages(prevMessages => [...prevMessages, { sender: 'ai', content: 'Sorry, there was an error processing your request.' }]);
    }
  };

  const handleGenerateModification = async () => {
    if (!selectedFile) return;

    try {
      const userId = sessionStorage.getItem('userId');
      const genModifyMessage = {
        sender: userId,
        content: 'Generate modification',
        timestamp: new Date().toISOString(),
        prevFile: selectedFile ? selectedFile.id : null,
        aiModifiedContent: aiModifiedContent
      };

      const response = await transitServerApi.getFileContent(genModifyMessage);
      setAiModifiedContent(response.data.content);
    } catch (error) {
      console.error('Error generating modification:', error);
    }
  };

  const handleSubmitModification = async () => {
    if (!selectedFile) return;

    try {
      const userId = sessionStorage.getItem('userId');
      const sendModifyMessage = {
        sender: userId,
        content: 'submit modified file',
        timestamp: new Date().toISOString(),
        prevFile: selectedFile ? selectedFile.id : null,
        aiModifiedContent: aiModifiedContent
      };

      await transitServerApi.getTrainData(sendModifyMessage);

      setSelectedFile(prev => ({ ...prev, content: aiModifiedContent }));
      updateFileSystem(selectedFile.id, { ...selectedFile, content: aiModifiedContent });
      setAiModifiedContent('');
    } catch (error) {
      console.error('Error submitting modification:', error);
    }
  };

  const handleCreateFile = async (parentId, fileName, isFolder = false) => {
    try {
      const response = await cloudDriveApi.createFile({ parentId, name: fileName, type: isFolder ? 'folder' : 'file' });
      const newFile = response.data.file;

      updateFileSystem(parentId, (folder) => ({
        ...folder,
        children: [...(folder.children || []), newFile]
      }));
    } catch (error) {
      console.error('Error creating file:', error);
    }
  };

  const handleDeleteFile = async (fileId) => {
    try {
      await cloudDriveApi.deleteFile(fileId);
      removeFileFromFileSystem(fileId);
    } catch (error) {
      console.error('Error deleting file:', error);
    }
  };

  const updateFileSystem = (parentId, updatedFile) => {
    setFileSystem(prevFileSystem => {
      const updateFile = (files) => {
        return files.map(file => {
          if (file.id === parentId) {
            return { ...file, ...updatedFile };
          }
          if (file.children) {
            return { ...file, children: updateFile(file.children) };
          }
          return file;
        });
      };

      return updateFile(prevFileSystem);
    });
  };

  const removeFileFromFileSystem = (fileId) => {
    setFileSystem(prevFileSystem => {
      const removeFile = (files) => {
        return files.reduce((acc, file) => {
          if (file.id === fileId) {
            return acc;
          }
          if (file.children) {
            return [...acc, { ...file, children: removeFile(file.children) }];
          }
          return [...acc, file];
        }, []);
      };

      return removeFile(prevFileSystem);
    });
  };

  return (
    <div className="app-container">
      <div className="left-panel">
        <FileTree
          fileSystem={fileSystem}
          onSelectFile={handleSelectFile}
          onCreateFile={handleCreateFile}
          onDeleteFile={handleDeleteFile}
        />
      </div>
      <div className="right-panel">
        {selectedFile ? (
          <Editor
            file={selectedFile}
            onSave={handleSaveContent}
            onGenerateModification={handleGenerateModification}
            onSubmitModification={handleSubmitModification}
            aiModifiedContent={aiModifiedContent}
          />
        ) : (
          <div className="no-file-selected">No file selected</div>
        )}
        <ChatBox
          messages={messages}
          onSendMessage={handleSendMessage}
        />
      </div>
    </div>
  );
}

export default App;
